private static boolean equalIncludingGenerics(ClassNode orig, ClassNode copy) {
  if (orig == copy) return true;
  if (orig.isGenericsPlaceHolder() != copy.isGenericsPlaceHolder()) return false;
  if (!orig.equals(copy)) return false;
  GenericsType[] gt1 = orig.getGenericsTypes();
  GenericsType[] gt2 = orig.getGenericsTypes();
  if ((gt1 == null || gt2 == null) && gt1 != gt2) return false;
  if (gt1 == gt2) return true;
  if (gt1.length != gt2.length) return false;
  for (int i = 0; i < gt1.length; i++) {
    if (!equalIncludingGenerics(gt1[i], gt2[i])) return false;
  }
  return true;
}

public <T extends Node> int insertChild(int index, final T child) {
  for (Node p = this; p != null; p = p.getParent()) {
    if (p == child) throw new GdxRuntimeException("Cannot add a parent as a child");
  }
  Node p = child.getParent();
  if (p != null && !p.removeChild(child))
    throw new GdxRuntimeException("Could not remove child from its current parent");
  if (index < 0 || index >= children.size) {
    index = children.size;
    children.add(child);
  } else children.insert(index, child);
  child.parent = this;
  return index;
}

protected Class<?>[] getInterfaces(Object object) {
  List<Class<?>> interfaceClasses = new ArrayList<Class<?>>();

  Class<?> clazz = object.getClass();

  getInterfaces(interfaceClasses, clazz);

  Class<?> superClazz = clazz.getSuperclass();

  while (superClazz != null) {
    getInterfaces(interfaceClasses, superClazz);

    superClazz = clazz.getSuperclass();
  }

  return interfaceClasses.toArray(new Class<?>[interfaceClasses.size()]);
}

private static TextRange findCustomRange(
    String text, int start, int end, char startChar, char endChar) {
  int prev = text.lastIndexOf(startChar, start);
  int next = text.indexOf(endChar, end);
  if (prev < 0 || next < 0) {
    return null;
  }
  if (StringUtil.contains(text, prev + 1, start, endChar)
      || StringUtil.contains(text, end, next, startChar)) return null;
  if (prev + 1 < start || next > end) {
    return new TextRange(prev + 1, next);
  }

  return new TextRange(prev, next + 1);
}

public static void removeChildren(ASTNode parent, @NotNull ASTNode first, @NotNull ASTNode last) {
  final boolean tailingElement =
      last.getStartOffset() + last.getTextLength()
          == parent.getStartOffset() + parent.getTextLength();
  final boolean forceReformat = needToForceReformat(parent, first, last);
  saveWhitespacesInfo(first);

  TreeElement child = (TreeElement) first;
  while (child != null) {
    // checkForOuters(child);
    if (child == last) break;
    child = child.getTreeNext();
  }
  assert child == last
      : last + " is not a successor of " + first + " in the .getTreeNext() chain";

  final ASTNode prevLeaf = TreeUtil.prevLeaf(first);
  final ASTNode nextLeaf = TreeUtil.nextLeaf(first);
  parent.removeRange(first, last.getTreeNext());
  ASTNode nextLeafToAdjust = nextLeaf;
  if (nextLeafToAdjust != null && prevLeaf != null && nextLeafToAdjust.getTreeParent() == null) {
    // next element has invalidated
    nextLeafToAdjust = prevLeaf.getTreeNext();
  }
  makePlaceHolderBetweenTokens(prevLeaf, nextLeafToAdjust, forceReformat, tailingElement);
}

