public MutableVariable getNodeByPath(
    @NotNull MutableVariable root, @NotNull List<String> relativeNodePath) {
  MutableVariable localRoot = root;
  for (int i = 0; i < relativeNodePath.size(); i++) {
    String path = relativeNodePath.get(i);
    if (localRoot != null) {
      List<MutableVariable> variables = new ArrayList<>(localRoot.getVariables());
      localRoot = null;
      for (int j = 0; j < variables.size(); j++) {
        MutableVariable variable = variables.get(j);
        if (variable.getName().equals(path)) {
          localRoot = variable;
          break;
        }
      }

      if (i == (relativeNodePath.size() - 1)) {
        return localRoot;
      }
    }
  }
  return null;
}

  private static void internalCopyDir(File src, File dest, boolean isOverride)
      throws IORuntimeException {
    if (false == dest.exists()) {
      dest.mkdirs();
    } else if (dest.isFile()) {
      throw new IORuntimeException(
          StrUtil.format(
              "Src [{}] is a directory but dest [{}] is a file!", src.getPath(), dest.getPath()));
    }

    final String files[] = src.list();
    for (String file : files) {
      File srcFile = new File(src, file);
      File destFile = new File(dest, file);
      // 递归复制
      if (srcFile.isDirectory()) {
        internalCopyDir(srcFile, destFile, isOverride);
      } else {
        internalCopyFile(srcFile, destFile, isOverride);
      }
    }
  }

