private static @Nullable Library findModuleLibrary(
    Module module,
    final @NotNull ModulesProvider provider,
    @NotNull String url,
    Set<Module> visited) {
  if (!visited.add(module)) {
    return null;
  }

  ModuleRootModel rootModel = provider.getRootModel(module);
  OrderEntry[] orderEntries = rootModel.getOrderEntries();
  for (OrderEntry orderEntry : orderEntries) {
    if (orderEntry instanceof LibraryOrderEntry) {
      LibraryOrderEntry libraryOrderEntry = ((LibraryOrderEntry) orderEntry);
      Library library = libraryOrderEntry.getLibrary();
      if (library == null) continue;
      String[] urls = library.getUrls(OrderRootType.CLASSES);
      if (urls.length != 1) continue;
      if (Comparing.strEqual(urls[0], url)) return library;
    } else if (orderEntry instanceof ModuleOrderEntry) {
      final Module dependency = ((ModuleOrderEntry) orderEntry).getModule();
      if (dependency == null) {
        continue;
      }
      final Library library = findModuleLibrary(dependency, provider, url, visited);
      if (library != null) {
        return library;
      }
    }
  }
  return null;
}

public void collect(int doc) throws IOException {
  int valuesDocID = leafOutcomeValue.docID();
  if (valuesDocID < doc) {
    valuesDocID = leafOutcomeValue.advance(valuesDocID);
  }
  int outcome;
  if (valuesDocID == doc) {
    outcome = (int) leafOutcomeValue.longValue();
  } else {
    outcome = 0;
  }

  outcome = trainingParams.positiveLabel == outcome ? 1 : 0;
  if (outcome == 1) {
    positiveDocsSet.set(context.docBase + doc);
  }
  docsSet.set(context.docBase + doc);
}

public void emitCode(CompilationResultBuilder crb, AArch64MacroAssembler masm) {
  AArch64Kind kind = (AArch64Kind) expectedValue.getPlatformKind();
  assert kind.isInteger();
  final int size = kind.getSizeInBytes() * Byte.SIZE;

  Register address = asRegister(addressValue);
  Register result = asRegister(resultValue);
  Register newVal = asRegister(newValue);
  if (AArch64LIRFlagsVersioned.useLSE(masm.target.arch)) {
    Register expected = asRegister(expectedValue);
    masm.mov(size, result, expected);
    masm.cas(size, expected, newVal, address, true /* acquire */, true /* release */);
    AArch64Compare.gpCompare(masm, resultValue, expectedValue);
  } else {
    // We could avoid using a scratch register here, by reusing resultValue for the
    // stlxr success flag and issue a mov resultValue, expectedValue in case of success
    // before returning.
    Register scratch = asRegister(scratchValue);
    Label retry = new Label();
    Label fail = new Label();
    masm.bind(retry);
    masm.ldaxr(size, result, address);
    AArch64Compare.gpCompare(masm, resultValue, expectedValue);
    masm.branchConditionally(AArch64Assembler.ConditionFlag.NE, fail);
    masm.stlxr(size, scratch, newVal, address);
    // if scratch == 0 then write successful, else retry.
    masm.cbnz(32, scratch, retry);
    masm.bind(fail);
  }
}

