private XmlTag addAnnotation(
    @NotNull XmlTag rootTag,
    @NotNull String ownerName,
    @NotNull ExternalAnnotation annotation,
    @Nullable XmlTag startTag) {
  if (startTag == null) {
    startTag = PsiTreeUtil.findChildOfType(rootTag, XmlTag.class);
  }

  XmlTag prevItem = null;
  XmlTag curItem = startTag;

  while (curItem != null) {
    XmlTag addedItem = addAnnotation(rootTag, ownerName, annotation, curItem, prevItem);
    if (addedItem != null) {
      return addedItem;
    }

    prevItem = curItem;
    curItem = PsiTreeUtil.getNextSiblingOfType(startTag, XmlTag.class);
  }

  return addItemTag(rootTag, prevItem, ownerName, annotation);
}

protected PageDTO evaluatePageRules(
    List<PageDTO> pageDTOList, Locale locale, Map<String, Object> ruleDTOs) {
  if (pageDTOList == null) {
    return NULL_PAGE;
  }

  // First check to see if we have a page that matches on the full locale.
  for (PageDTO page : pageDTOList) {
    if (locale != null && locale.getLocaleCode() != null) {
      if (locale.getLocaleCode().equals(page.getLocaleCode())) {
        if (passesPageRules(page, ruleDTOs)) {
          return page;
        }
      }
    }
  }

  // Otherwise, we look for a match using just the language.
  for (PageDTO page : pageDTOList) {
    if (passesPageRules(page, ruleDTOs)) {
      return page;
    }
  }

  return NULL_PAGE;
}

