public void execute(BstEntry context) {
  Stack<Object> stack = vm.getStack();

  if (stack.size() < 2) {
    throw new VMException("Not enough operands on stack for operation text.prefix$");
  }
  Object o1 = stack.pop();
  Object o2 = stack.pop();

  if (!(o1 instanceof Integer)) {
    vm.warn("An integer is needed as first parameter to text.prefix$");
    stack.push("");
    return;
  }
  if (!(o2 instanceof String)) {
    vm.warn("A string is needed as second parameter to text.prefix$");
    stack.push("");
    return;
  }

  stack.push(BibtexTextPrefix.textPrefix((Integer) o1, (String) o2, vm));
}

public void stack(
    Player player,
    EditSession editSession,
    LocalSession session,
    @Selection Region region,
    @Optional("1") @Range(min = 1) int count,
    @Optional(Direction.AIM) @Direction Vector direction,
    @Switch('s') boolean moveSelection,
    @Switch('a') boolean ignoreAirBlocks)
    throws WorldEditException {
  int affected = editSession.stackCuboidRegion(region, direction, count, !ignoreAirBlocks);

  if (ignoreAirBlocks) {
    try {
      final Vector size = region.getMaximumPoint().subtract(region.getMinimumPoint());

      final Vector shiftVector = direction.multiply(count * (Math.abs(direction.dot(size)) + 1));
      region.shift(shiftVector);

      session.getRegionSelector(player.getWorld()).learnChanges();
      session.getRegionSelector(player.getWorld()).explainRegionAdjust(player, session);
    } catch (RegionOperationException e) {
      player.printError(e.getMessage());
    }
  }

  player.print(affected + " blocks changed. Undo with //undo");
}

