public INDArray calculateDelta(INDArray epsilon) {
  INDArray z = preOutput(true);
  INDArray activationDerivative =
      Nd4j.getExecutioner()
          .execAndReturn(
              Nd4j.getOpFactory()
                  .createTransform(conf().getActivationFunction(), z)
                  .derivative());
  if (!Arrays.equals(z.shape(), activationDerivative.shape()))
    throw new IllegalStateException("Shapes must be same");
  return epsilon.muli(activationDerivative);
}

private void cancelEndOfWindowAndGarbageCollectionTimers(
    ReduceFn<?, ?, ?, W>.Context directContext) {
  WindowTracing.debug(
      "ReduceFnRunner.cancelEndOfWindowAndGarbageCollectionTimers: Deleting timers for "
          + "key:{}; window:{} where inputWatermark:{}; outputWatermark:{}",
      key,
      directContext.window(),
      timerInternals.currentInputWatermarkTime(),
      timerInternals.currentOutputWatermarkTime());
  Instant eow = directContext.window().maxTimestamp();
  directContext.timers().deleteTimer(eow, TimeDomain.EVENT_TIME);
  Instant gc = garbageCollectionTime(directContext.window());
  if (gc.isAfter(eow)) {
    directContext.timers().deleteTimer(eow, TimeDomain.EVENT_TIME);
  }
}

void reorderProbation(Node<K, V> node) {
  if (!accessOrderProbationDeque().contains(node)) {
    // Ignore stale accesses for an entry that is no longer present
    return;
  } else if (node.getPolicyWeight() > mainProtectedMaximum()) {
    return;
  }

  long mainProtectedWeightedSize = mainProtectedWeightedSize() + node.getPolicyWeight();
  accessOrderProbationDeque().remove(node);
  accessOrderProtectedDeque().add(node);
  node.makeMainProtected();

  long mainProtectedMaximum = mainProtectedMaximum();
  while (mainProtectedWeightedSize > mainProtectedMaximum) {
    Node<K, V> demoted = accessOrderProtectedDeque().pollFirst();
    if (demoted == null) {
      break;
    }
    demoted.makeMainProbation();
    accessOrderProbationDeque().add(demoted);
    mainProtectedWeightedSize -= demoted.getPolicyWeight();
  }

  lazySetMainProtectedWeightedSize(mainProtectedWeightedSize);
}

public LabelledDocument nextDocument() {
  LabelledDocument document = new LabelledDocument();

  document.setContent(backendIterator.nextSentence());
  if (backendIterator instanceof LabelAwareSentenceIterator) {
    List<String> labels = ((LabelAwareSentenceIterator) backendIterator).currentLabels();
    if (labels != null) {
      for (String label : labels) {
        document.addLabel(label);
        generator.storeLabel(label);
      }
    } else {
      String label = ((LabelAwareSentenceIterator) backendIterator).currentLabel();
      if (label != null) {
        document.addLabel(label);
        generator.storeLabel(label);
      }
    }
  } else if (generator != null) document.addLabel(generator.nextLabel());

  return document;
}

private static GlobalInspectionToolWrapper getProblemDescriptionProcessor(
    @NotNull final GlobalInspectionToolWrapper toolWrapper,
    final Map<String, DescriptorProviderInspection> wrappersMap) {

  return new GlobalInspectionToolWrapper(toolWrapper.getTool()) {
    @Override
    public void addProblemElement(
        RefEntity refEntity, CommonProblemDescriptor... commonProblemDescriptors) {
      for (CommonProblemDescriptor problemDescriptor : commonProblemDescriptors) {
        if (problemDescriptor instanceof ProblemDescriptor) {
          String problemGroup = ((ProblemDescriptor) problemDescriptor).getProblemGroup();

          if (problemGroup != null) {
            DescriptorProviderInspection dummyWrapper = wrappersMap.get(problemGroup);

            if (dummyWrapper != null) { // Else it's switched off
              dummyWrapper.addProblemElement(refEntity, problemDescriptor);
            }
          } else {
            toolWrapper.addProblemElement(refEntity, problemDescriptor);
          }
        }
      }
    }
  };
}

  public TiledDrawable getTiledDrawable(String name) {
    TiledDrawable tiled = optional(name, TiledDrawable.class);
    if (tiled != null) return tiled;

    Drawable drawable = optional(name, Drawable.class);
    if (tiled != null) {
      if (!(drawable instanceof TiledDrawable)) {
        throw new GdxRuntimeException(
            "Drawable found but is not a TiledDrawable: "
                + name
                + ", "
                + drawable.getClass().getName());
      }
      return tiled;
    }

    tiled = new TiledDrawable(getRegion(name));
    add(name, tiled, TiledDrawable.class);
    return tiled;
  }

public static Class getMirrorOrFail(ResolvedJavaType type, ClassLoader loader)
    throws NoClassDefFoundError {
  ResolvedJavaType elementalType = getElementalType(type);
  Class elementalClass;
  if (elementalType.isPrimitive()) {
    elementalClass = type.getKind().toJavaClass();
  } else {
    try {
      elementalClass = Class.forName(toJavaName(elementalType), true, loader);
    } catch (ClassNotFoundException e) {
      throw (NoClassDefFoundError) new NoClassDefFoundError().initCause(e);
    }
  }
  if (type.isArray()) {
    ResolvedJavaType t = type;
    while (t.getComponentType() != null) {
      elementalClass = Array.newInstance(elementalClass, 0).getClass();
      t = t.getComponentType();
    }
  }
  assert elementalClass != null : toJavaName(type);
  return elementalClass;
}

private void addResolveListDedupe(
    List<ResolvedComponentInfo> into, Intent intent, List<ResolveInfo> from) {
  final int fromCount = from.size();
  final int intoCount = into.size();
  for (int i = 0; i < fromCount; i++) {
    final ResolveInfo newInfo = from.get(i);
    boolean found = false;
    // Only loop to the end of into as it was before we started; no dupes in from.
    for (int j = 0; j < intoCount; j++) {
      final ResolvedComponentInfo rci = into.get(j);
      if (isSameResolvedComponent(newInfo, rci)) {
        found = true;
        rci.add(intent, newInfo);
        break;
      }
    }
    if (!found) {
      final ComponentName name =
          new ComponentName(newInfo.activityInfo.packageName, newInfo.activityInfo.name);
      final ResolvedComponentInfo rci = new ResolvedComponentInfo(name, intent, newInfo);
      rci.setPinned(isComponentPinned(name));
      into.add(rci);
    }
  }
}

private static void addToResourceCollector(
    AaptResourceCollector resourceCollector,
    ResourceDirectory resourceDirectory,
    Node node,
    RType rType,
    String resourceValue) {
  String resourceName = sanitizeName(rType, resourceCollector, extractNameAttribute(node));
  resourceCollector.addRTypeResourceName(rType, resourceName, resourceValue, resourceDirectory);
  if (rType.equals(RType.STYLEABLE)) {

    int count = 0;
    for (Node attrNode = node.getFirstChild();
        attrNode != null;
        attrNode = attrNode.getNextSibling()) {
      if (attrNode.getNodeType() != Node.ELEMENT_NODE || !attrNode.getNodeName().equals("attr")) {
        continue;
      }

      String rawAttrName = extractNameAttribute(attrNode);
      String attrName = sanitizeName(rType, resourceCollector, rawAttrName);
      resourceCollector.addResource(
          RType.STYLEABLE,
          IdType.INT,
          String.format("%s_%s", resourceName, attrName),
          Integer.toString(count++));

      if (!rawAttrName.startsWith("android:")) {
        resourceCollector.addIntResourceIfNotPresent(RType.ATTR, attrName);
        resourceCollector.addRTypeResourceName(
            RType.ATTR, rawAttrName, nodeToString(attrNode, true), resourceDirectory);
      }
    }

    resourceCollector.addIntArrayResourceIfNotPresent(rType, resourceName, count);
  } else {
    resourceCollector.addIntResourceIfNotPresent(rType, resourceName);
  }
}

private void validateCatch(Node n) {
  validateNodeType(Token.CATCH, n);
  validateChildCount(n);
  Node caught = n.getFirstChild();
  if (caught.isName()) {
    validateName(caught);
  } else if (n.isArrayPattern()) {
    validateArrayPattern(Token.CATCH, caught);
  } else {
    validateObjectPattern(Token.CATCH, caught);
  }
  validateBlock(n.getLastChild());
}

public final boolean blacklist(int index, long blacklistDurationMs) {
  long nowMs = SystemClock.elapsedRealtime();
  boolean canBlacklist = isBlacklisted(index, nowMs);
  for (int i = 0; i < length && !canBlacklist; i++) {
    canBlacklist = i != index && !isBlacklisted(i, nowMs);
  }
  if (!canBlacklist) {
    return false;
  }
  blacklistUntilTimes[index] = Math.max(blacklistUntilTimes[index], nowMs + blacklistDurationMs);
  return true;
}

