private boolean hasSimpleSchemaType(String type) {
  final String attributeValue = myTag.getAttributeValue("type");

  if (attributeValue != null) {
    if (attributeValue.endsWith(type)) {
      final String namespacePrefix = myTag.getNamespacePrefix();

      if (namespacePrefix.length() > 0) {
        return attributeValue.equals(namespacePrefix + ":" + type);
      } else {
        return true;
      }
    }
  }

  return false;
}

private String resolvePlaceholders(String value) {
  StringBuffer result = new StringBuffer();
  Matcher matcher = PLACEHOLDER_PATTERN.matcher(value);

  while (matcher.find()) {
    String placeholder = matcher.group(1);
    String replacement =
        placeholder.startsWith("env.")
            ? environmentVariables.get(placeholder.substring(4))
            : systemProperties.get(placeholder);
    if (replacement == null) {
      throw new CannotLocateLocalMavenRepositoryException(
          String.format("Cannot resolve placeholder '%s' in value '%s'", placeholder, value));
    }
    matcher.appendReplacement(result, Matcher.quoteReplacement(replacement));
  }
  matcher.appendTail(result);

  return result.toString();
}

  protected void closeReport() {
    int counter = 1;

    if (!sinks.isEmpty()) {
      for (Iterator i = sinks.iterator(); i.hasNext(); ) {
        MultiPageSink currentSink = (MultiPageSink) i.next();

        currentSink.paragraph();
        for (Iterator j = sinks.iterator(); i.hasNext(); ) {
          if (counter > 1) {
            currentSink.text("&nbsp;");
          }
          MultiPageSink sink = (MultiPageSink) i.next();
          sink.link(sink.getOutputName() + ".html");
          sink.text(String.valueOf(counter++));
