  private String substituteKeywords(
      String message, String dialNumber, String telVoice, String meetingName) {
    String welcomeMessage = message;

    String DIAL_NUM = "%%DIALNUM%%";
    String CONF_NUM = "%%CONFNUM%%";
    String CONF_NAME = "%%CONFNAME%%";
    ArrayList<String> keywordList = new ArrayList<String>();
    keywordList.add(DIAL_NUM);
    keywordList.add(CONF_NUM);
    keywordList.add(CONF_NAME);

    Iterator<String> itr = keywordList.iterator();
    while (itr.hasNext()) {
      String keyword = (String) itr.next();
      if (keyword.equals(DIAL_NUM)) {
        welcomeMessage = welcomeMessage.replaceAll(DIAL_NUM, dialNumber);
      } else if (keyword.equals(CONF_NUM)) {
        welcomeMessage = welcomeMessage.replaceAll(CONF_NUM, telVoice);
      } else if (keyword.equals(CONF_NAME)) {
        welcomeMessage = welcomeMessage.replaceAll(CONF_NAME, meetingName);
      }
    }
    return welcomeMessage;
  }

public void afterExecution(Action action, Token token, MetadataHandler metadataHandler)
    throws IOException {
  Preconditions.checkArgument(token != null);
  String key = token.cacheKey;
  if (actionCache.get(key) != null) {
    // This cache entry has already been updated by a shared action. We don't need to do it again.
    return;
  }
  ActionCache.Entry entry = actionCache.createEntry(action.getKey());
  for (Artifact output : action.getOutputs()) {
    // Remove old records from the cache if they used different key.
    String execPath = output.getExecPathString();
    if (!key.equals(execPath)) {
      actionCache.remove(key);
    }
    if (!metadataHandler.artifactOmitted(output)) {
      // Output files *must* exist and be accessible after successful action execution.
      Metadata metadata = metadataHandler.getMetadata(output);
      Preconditions.checkState(metadata != null);
      entry.addFile(output.getExecPath(), metadata);
    }
  }
  for (Artifact input : action.getInputs()) {
    entry.addFile(input.getExecPath(), metadataHandler.getMetadataMaybe(input));
  }
  entry.getFileDigest();
  actionCache.put(key, entry);
}

