public Subscription onSubscribe(Observer<? super T> actualObserver) {
  CompositeSubscription completeSubscription = new CompositeSubscription();

  /**
   * We must synchronize a merge because we subscribe to multiple sequences in parallel that will
   * each be emitting.
   *
   * <p>The calls from each sequence must be serialized.
   *
   * <p>Bug report: https://github.com/Netflix/RxJava/issues/614
   */
  SafeObservableSubscription subscription = new SafeObservableSubscription(ourSubscription);
  completeSubscription.add(subscription);
  SynchronizedObserver<T> synchronizedObserver =
      new SynchronizedObserver<T>(actualObserver, subscription);

  /** Subscribe to the parent Observable to get to the children Observables */
  completeSubscription.add(sequences.subscribe(new ParentObserver(synchronizedObserver)));

  /* return our subscription to allow unsubscribing */
  return completeSubscription;
}

public StreamSinkChannel create() {
  final ChannelWrapper[] wrappers = wrappersUpdater.getAndSet(this, null);
  if (wrappers == null) {
    return null;
  }
  StreamSinkChannel oldChannel = firstChannel;
  StreamSinkChannel channel = oldChannel;
  for (ChannelWrapper wrapper : wrappers) {
    channel = ((ChannelWrapper<StreamSinkChannel>) wrapper).wrap(oldChannel, exchange);
    if (channel == null) {
      channel = oldChannel;
    }
  }
  exchange.startResponse();
  return channel;
}

