public static SegmentReplicantLookup make(DruidCluster cluster) {
  final Table<String, String, Integer> segmentsInCluster = HashBasedTable.create();
  final Table<String, String, Integer> loadingSegments = HashBasedTable.create();

  for (MinMaxPriorityQueue<ServerHolder> serversByType : cluster.getSortedServersByTier()) {
    for (ServerHolder serverHolder : serversByType) {
      DruidServer server = serverHolder.getServer();

      for (DataSegment segment : server.getSegments().values()) {
        Integer numReplicants = segmentsInCluster.get(segment.getIdentifier(), server.getTier());
        if (numReplicants == null) {
          numReplicants = 0;
        }
        segmentsInCluster.put(segment.getIdentifier(), server.getTier(), ++numReplicants);
      }

      // Also account for queued segments
      for (DataSegment segment : serverHolder.getPeon().getSegmentsToLoad()) {
        Integer numReplicants = loadingSegments.get(segment.getIdentifier(), server.getTier());
        if (numReplicants == null) {
          numReplicants = 0;
        }
        loadingSegments.put(segment.getIdentifier(), server.getTier(), ++numReplicants);
      }
    }
  }

  return new SegmentReplicantLookup(segmentsInCluster, loadingSegments);
}

public ClassNode resolveType(final Expression exp, final ClassNode current) {
  Expression target =
      exp instanceof VariableExpression ? getTarget((VariableExpression) exp) : exp;
  ClassNode dif = (ClassNode) target.getNodeMetaData(StaticTypesMarker.DECLARATION_INFERRED_TYPE);
  if (dif != null) return dif;
  ClassNode inferredType = (ClassNode) target.getNodeMetaData(StaticTypesMarker.INFERRED_TYPE);
  if (inferredType != null) {
    return inferredType;
  }
  return super.resolveType(exp, current);
}

protected Object createBoxLayout(Map properties) {
  Object parent = getCurrent();
  if (parent instanceof Container) {
    Object axisObject = properties.remove("axis");
    int axis = BoxLayout.X_AXIS;
    if (axisObject != null) {
      Integer i = (Integer) axisObject;
      axis = i.intValue();
    }

    Container target = (Container) parent;
    if (target instanceof RootPaneContainer) {
      target = ((RootPaneContainer) target).getContentPane();
    }
    BoxLayout answer = new BoxLayout(target, axis);

    // now let's try to set the layout property
    InvokerHelper.setProperty(parent, "layout", answer);
    return answer;
  } else {
    throw new RuntimeException("Must be nested inside a Container");
  }
}

protected void sendLocallyUpdated(SyncData<TYPE> data) throws IOException {
  int length;
  length = data.localUpdated.getCount();
  for (int i = 0; i < length; i++) {
    data.localUpdated.moveToNext();
    TYPE local = read(data.localUpdated);
    try {
      if (local.task == null) continue;

      // if there is a conflict, merge
      int remoteIndex = matchTask((ArrayList<TYPE>) data.remoteUpdated, local);
      if (remoteIndex != -1) {
        TYPE remote = data.remoteUpdated.get(remoteIndex);
        push(local, remote);

        // re-read remote task after merge
        remote = pull(remote);
        remote.task.setId(local.task.getId());
        data.remoteUpdated.set(remoteIndex, remote);
      } else {
        push(local, null);
      }
    } catch (Exception e) {
      handleException("sync-local-updated", e, false); // $NON-NLS-1$
    }
    write(local);
  }
}

protected static FrequencySet<String> combineMax(FrequencySet<String> a, FrequencySet<String> b) {
  FrequencySet<String> result = combineAndClip(a, b, 1);
  for (Map.Entry<String, MutableInt> entry : a.entrySet()) {
    result.get(entry.getKey()).v = entry.getValue().v;
  }

  for (Map.Entry<String, MutableInt> entry : a.entrySet()) {
    MutableInt slot = result.get(entry.getKey());
    slot.v = Math.max(slot.v, entry.getValue().v);
  }

  return result;
}

