public static void remove(CuratorFramework curator, String path, String value) throws Exception {
  if (curator.checkExists().forPath(path) != null) {
    List<String> parts = new LinkedList<String>();
    String data = getStringData(curator, path);
    if (data != null) {
      parts = new ArrayList<String>(Arrays.asList(data.trim().split(" +")));
    }
    boolean changed = false;
    StringBuilder sb = new StringBuilder();
    for (Iterator<String> it = parts.iterator(); it.hasNext(); ) {
      String v = it.next();
      if (v.matches(value)) {
        it.remove();
        changed = true;
      }
    }
    if (changed) {
      sb.delete(0, sb.length());
      for (String part : parts) {
        if (sb.length() > 0) {
          sb.append(" ");
        }
        sb.append(part);
      }
      setData(curator, path, sb.toString());
    }
  }
}

private boolean canSeeAllInterfaces(ClassLoader loader, Class<?>[] interfaces) {
  for (Class<?> currentInterface : interfaces) {
    String ifName = currentInterface.getName();
    try {
      Class<?> ifClass = Class.forName(ifName, true, loader);
      if (ifClass != currentInterface) {
        return false;
      }
      // we need to check all the params/returns as well as the Proxy creation
      // will try to create methods for all of this even if they aren't used
      // by the client and not available in the clients classloader
      for (Method m : ifClass.getMethods()) {
        Class<?> returnType = m.getReturnType();
        if (!returnType.isPrimitive()) {
          Class.forName(returnType.getName(), true, loader);
        }
        for (Class<?> p : m.getParameterTypes()) {
          if (!returnType.isPrimitive()) {
            Class.forName(p.getName(), true, loader);
          }
        }
      }
    } catch (NoClassDefFoundError e) {
      return false;
    } catch (ClassNotFoundException e) {
      return false;
    }
  }
  return true;
}

public static int partValue(int total, int partCount, boolean isPlusOneWhenHasRem) {
  int partValue = 0;
  if (total % partCount == 0) {
    total = total / partCount;
  } else {
    partValue = (int) Math.floor(total / partCount);
    if (isPlusOneWhenHasRem) {
      partValue += 1;
    }
  }
  return partValue;
}

public void testInterceptedMethodThrows() throws Exception {
  Injector injector =
      Guice.createInjector(
          new AbstractModule() {
            protected void configure() {
              bindInterceptor(Matchers.any(), Matchers.any(), new CountingInterceptor());
              bindInterceptor(Matchers.any(), Matchers.any(), new CountingInterceptor());
            }
          });

  Interceptable interceptable = injector.getInstance(Interceptable.class);
  try {
    interceptable.explode();
    fail();
  } catch (Exception e) {
    // validate all causes.
    for (Throwable t = e; t != null; t = e.getCause()) {
      StackTraceElement[] stackTraceElement = t.getStackTrace();
      assertEquals("explode", stackTraceElement[0].getMethodName());
      assertEquals("invoke", stackTraceElement[1].getMethodName());
      assertEquals("invoke", stackTraceElement[2].getMethodName());
      assertEquals("testInterceptedMethodThrows", stackTraceElement[3].getMethodName());
    }
  }
}

