public boolean isAvailable(@NotNull Project project, Editor editor, @NotNull PsiElement element) {
  if (!(element instanceof PsiJavaToken)) {
    return false;
  }

  final PsiJavaToken token = (PsiJavaToken) element;

  if (token.getTokenType() != JavaTokenType.STRING_LITERAL) {
    return false;
  }

  final String text = token.getText();
  if (text == null) {
    return false;
  }

  final int indexOfSlashN = text.indexOf("\\n");
  final int indexOfSlashNSlashR = text.indexOf("\\n\\r");

  if (indexOfSlashN == -1
      || Comparing.equal(text.substring(indexOfSlashN, text.length()), "\\n\"")) {
    return false;
  }

  if (indexOfSlashNSlashR != -1
      && Comparing.equal(text.substring(indexOfSlashNSlashR, text.length()), "\\n\\r\"")) {
    return false;
  }

  return true;
}

