public static <F> boolean setOnce(
    AtomicReferenceFieldUpdater<F, Subscription> field, F instance, Subscription s) {
  Subscription a = field.get(instance);
  if (a == CancelledSubscription.INSTANCE) {
    s.cancel();
    return false;
  }
  if (a != null) {
    s.cancel();
    reportSubscriptionSet();
    return false;
  }

  if (field.compareAndSet(instance, null, s)) {
    return true;
  }

  a = field.get(instance);

  if (a == CancelledSubscription.INSTANCE) {
    s.cancel();
    return false;
  }

  s.cancel();
  reportSubscriptionSet();
  return false;
}

