public List<ExecutionResult> succeeds(MSBuildAction action) {
  withArgument(toTargetArgument(action));
  ExecOutput result = findMSBuild().execute(args, buildEnvironment(workingDir));
  System.out.println(result.getOut());
  String output = trimLines(result.getOut());
  String error = trimLines(result.getError());
  List<ExecutionResult> results = new ArrayList<ExecutionResult>();
  int first = output.indexOf(SEPARATOR);
  if (first < 0) {
    return Collections.emptyList();
  }
  output = output.substring(first + SEPARATOR.length());
  while (output.length() > 0) {
    int next = output.indexOf(SEPARATOR);
    if (next < 0) {
      results.add(OutputScrapingExecutionResult.from(output, error));
      output = "";
    } else {
      results.add(OutputScrapingExecutionResult.from(output.substring(0, next), error));
      output = output.substring(next + SEPARATOR.length());
    }
    error = "";
  }
  return results;
}

public static String getModuleDir(@NotNull String moduleFilePath) {
  String moduleDir = PathUtilRt.getParentPath(moduleFilePath);
  if (StringUtil.isEmpty(moduleDir)) {
    return null;
  }

  // hack so that, if a module is stored inside the .idea directory, the base directory
  // rather than the .idea directory itself is considered the module root
  // (so that a Ruby IDE project doesn't break if its directory is moved together with the .idea
  // directory)
  String moduleDirParent = PathUtilRt.getParentPath(moduleDir);
  if (!StringUtil.isEmpty(moduleDirParent)
      && PathUtilRt.getFileName(moduleDir).equals(DIRECTORY_STORE_NAME)) {
    moduleDir = moduleDirParent;
  }
  moduleDir = toSystemIndependentName(moduleDir);
  if (moduleDir.endsWith(":/")) {
    moduleDir = moduleDir.substring(0, moduleDir.length() - 1);
  }
  return moduleDir;
}

