private List<AnnotationData> doCollect(
    @NotNull PsiModifierListOwner listOwner, boolean onlyWritable) {
  final List<PsiFile> files = findExternalAnnotationsFiles(listOwner);
  if (files == null) {
    return NO_DATA;
  }
  SmartList<AnnotationData> result = new SmartList<AnnotationData>();
  String externalName = getExternalName(listOwner, false);
  if (externalName == null) return NO_DATA;
  String oldExternalName = getNormalizedExternalName(listOwner);

  for (PsiFile file : files) {
    if (!file.isValid()) continue;
    if (onlyWritable && !file.isWritable()) continue;

    MostlySingularMultiMap<String, AnnotationData> fileData = getDataFromFile(file);

    addAnnotations(result, externalName, file, fileData);
    if (oldExternalName != null && !externalName.equals(oldExternalName)) {
      addAnnotations(result, oldExternalName, file, fileData);
    }
  }
  if (result.isEmpty()) {
    return NO_DATA;
  }
  result.trimToSize();
  return result;
}

public ModelNode execute(
    final ModelNode operation,
    final OperationMessageHandler handler,
    final OperationTransactionControl control,
    final OperationAttachments attachments) {
  final ModelNode headers =
      operation.has(OPERATION_HEADERS) ? operation.get(OPERATION_HEADERS) : null;
  final boolean rollbackOnFailure =
      headers == null
          || !headers.hasDefined(ROLLBACK_ON_RUNTIME_FAILURE)
          || headers.get(ROLLBACK_ON_RUNTIME_FAILURE).asBoolean();
  final EnumSet<NewOperationContextImpl.ContextFlag> contextFlags =
      rollbackOnFailure
          ? EnumSet.of(NewOperationContextImpl.ContextFlag.ROLLBACK_ON_FAIL)
          : EnumSet.noneOf(NewOperationContextImpl.ContextFlag.class);
  NewOperationContextImpl context =
      new NewOperationContextImpl(
          this,
          controllerType,
          contextFlags,
          handler,
          attachments,
          modelReference.get(),
          control,
          processState,
          bootingFlag.get());
  ModelNode response = new ModelNode();
  context.addStep(response, operation, prepareStep, NewOperationContext.Stage.MODEL);
  RB_ON_RT_FAILURE.set(Boolean.valueOf(rollbackOnFailure));
  try {
    context.completeStep();
  } finally {
    RB_ON_RT_FAILURE.set(null);
  }
  ControlledProcessState.State state = processState.getState();
  switch (state) {
    case RELOAD_REQUIRED:
    case RESTART_REQUIRED:
      response.get(RESPONSE_HEADERS, PROCESS_STATE).set(state.toString());
      break;
    default:
      break;
  }
  return response;
}

private static File createTempFile(Context context, @Nullable String mimeType)
    throws IOException {
  File externalCacheDir = context.getExternalCacheDir();
  File internalCacheDir = context.getCacheDir();
  File cacheDir;
  if (externalCacheDir == null && internalCacheDir == null) {
    throw new IOException("No cache directory available");
  }
  if (externalCacheDir == null) {
    cacheDir = internalCacheDir;
  } else if (internalCacheDir == null) {
    cacheDir = externalCacheDir;
  } else {
    cacheDir =
        externalCacheDir.getFreeSpace() > internalCacheDir.getFreeSpace()
            ? externalCacheDir
            : internalCacheDir;
  }
  return File.createTempFile(TEMP_FILE_PREFIX, getFileExtensionForType(mimeType), cacheDir);
}

protected MediaType selectMediaType(
    ServerWebExchange exchange, Supplier<List<MediaType>> producibleTypesSupplier) {

  List<MediaType> acceptableTypes = getAcceptableTypes(exchange);
  List<MediaType> producibleTypes = getProducibleTypes(exchange, producibleTypesSupplier);

  Set<MediaType> compatibleMediaTypes = new LinkedHashSet<>();
  for (MediaType acceptable : acceptableTypes) {
    for (MediaType producible : producibleTypes) {
      if (acceptable.isCompatibleWith(producible)) {
        compatibleMediaTypes.add(selectMoreSpecificMediaType(acceptable, producible));
      }
    }
  }

  List<MediaType> result = new ArrayList<>(compatibleMediaTypes);
  MediaType.sortBySpecificityAndQuality(result);

  for (MediaType mediaType : result) {
    if (mediaType.isConcrete()) {
      return mediaType;
    } else if (mediaType.equals(MediaType.ALL) || mediaType.equals(MEDIA_TYPE_APPLICATION_ALL)) {
      return MediaType.APPLICATION_OCTET_STREAM;
    }
  }

  return null;
}

