public static <T> T getFirstImplementation(final T t) {
  T cur = t;
  while (cur instanceof MergedObject) {
    final List<T> implementations = ((MergedObject<T>) cur).getImplementations();
    cur = implementations.isEmpty() ? null : implementations.get(0);
  }
  return cur;
}

public static String getShortestClassName(PsiClass aClass, PsiElement context) {
  String shortName = aClass.getName();
  PsiClass containingClass = aClass.getContainingClass();
  while (containingClass != null) {
    shortName = containingClass.getName() + "." + shortName;
    containingClass = aClass.getContainingClass();
  }
  LOG.assertTrue(shortName != null);

  String qName = aClass.getQualifiedName();
  if (qName == null) return shortName;

  final PsiManager manager = aClass.getManager();
  return manager.areElementsEquivalent(
          aClass, manager.getResolveHelper().resolveReferencedClass(shortName, context))
      ? shortName
      : qName;
}

