void removeReferences(Node node) {
  if (DefinitionsRemover.isDefinitionNode(node)) {
    DefinitionSite defSite = definitionNodeByDefinitionSite.get(node);
    if (defSite != null) {
      Definition def = defSite.definition;
      String name = getSimplifiedName(def.getLValue());
      if (name != null) {
        this.definitionNodeByDefinitionSite.remove(node);
        this.nameDefinitionMultimap.remove(name, node);
      }
    }
  } else {
    Node useSite = node;
    if (useSite.isGetProp()) {
      String propName = useSite.getLastChild().getString();
      if (propName.equals("apply") || propName.equals("call")) {
        useSite = useSite.getFirstChild();
      }
    }
    String name = getSimplifiedName(useSite);
    if (name != null) {
      this.nameUseSiteMultimap.remove(name, new UseSite(useSite, null, null));
    }
  }

  for (Node child : node.children()) {
    removeReferences(child);
  }
}

public void onFling(int velocityX, int velocityY) {
  int velocity = orientationHelper.getFlingVelocity(velocityX, velocityY);
  int throttleValue = shouldSlideOnFling ? Math.abs(velocity / flingThreshold) : 1;
  int newPosition = currentPosition + Direction.fromDelta(velocity).applyTo(throttleValue);
  newPosition = checkNewOnFlingPositionIsInBounds(newPosition);
  boolean isInScrollDirection = velocity * scrolled >= 0;
  boolean canFling = isInScrollDirection && isInBounds(newPosition);
  if (canFling) {
    startSmoothPendingScroll(newPosition);
  } else {
    returnToCurrentPosition();
  }
}

private static int replaceWhiteSpace(
    final FormattingModel model,
    @NotNull final LeafBlockWrapper block,
    int shift,
    final CharSequence _newWhiteSpace,
    final CommonCodeStyleSettings.IndentOptions options) {
  final WhiteSpace whiteSpace = block.getWhiteSpace();
  final TextRange textRange = whiteSpace.getTextRange();
  final TextRange wsRange = shiftRange(textRange, shift);
  final String newWhiteSpace = _newWhiteSpace.toString();
  TextRange newWhiteSpaceRange =
      model instanceof FormattingModelEx
          ? ((FormattingModelEx) model).replaceWhiteSpace(wsRange, block.getNode(), newWhiteSpace)
          : model.replaceWhiteSpace(textRange, newWhiteSpace);

  shift += newWhiteSpaceRange.getLength() - textRange.getLength();

  if (block.isLeaf() && whiteSpace.containsLineFeeds() && block.containsLineFeeds()) {
    final TextRange currentBlockRange = shiftRange(block.getTextRange(), shift);

    IndentInside oldBlockIndent = whiteSpace.getInitialLastLineIndent();
    IndentInside whiteSpaceIndent =
        IndentInside.createIndentOn(IndentInside.getLastLine(newWhiteSpace));
    final int shiftInside = calcShift(oldBlockIndent, whiteSpaceIndent, options);

    if (shiftInside != 0 || !oldBlockIndent.equals(whiteSpaceIndent)) {
      final TextRange newBlockRange =
          model.shiftIndentInsideRange(currentBlockRange, shiftInside);
      shift += newBlockRange.getLength() - block.getLength();
    }
  }
  return shift;
}

public void addAllUpdates(LeftTupleSets tupleSets) {
  LeftTupleSetsImpl tupleSetsImpl = (LeftTupleSetsImpl) tupleSets;
  if (tupleSetsImpl.getUpdateFirst() != null) {
    if (updateFirst == null) {
      updateFirst = tupleSetsImpl.getUpdateFirst();
      updateSize = tupleSetsImpl.updateSize;
    } else {
      LeftTuple current = updateFirst;
      LeftTuple last = null;
      while (current != null) {
        last = current;
        current = current.getStagedNext();
      }
      LeftTuple leftTuple = tupleSetsImpl.getUpdateFirst();
      last.setStagedNext(leftTuple);
      leftTuple.setStagePrevious(leftTuple);
      updateSize = updateSize + tupleSetsImpl.updateSize();
    }
    tupleSetsImpl.updateFirst = null;
    tupleSetsImpl.updateSize = 0;
  }
}

