private static List<TypeConstraint> addExpectedConstructorParameters(
    GrExpression expression, GrListOrMap list) {
  final PsiType listType = list.getType();
  if (!(listType instanceof GrTupleType)) {
    return Collections.emptyList();
  }

  final PsiType[] argTypes = ((GrTupleType) listType).getComponentTypes();
  final int argIndex = Arrays.asList(list.getInitializers()).indexOf(expression);
  assert argIndex >= 0;

  final ArrayList<TypeConstraint> result = new ArrayList<TypeConstraint>();
  for (PsiType type : GroovyExpectedTypesProvider.getDefaultExpectedTypes(list)) {
    if (type instanceof PsiClassType) {
      for (GroovyResolveResult resolveResult :
          PsiUtil.getConstructorCandidates((PsiClassType) type, argTypes, list)) {
        final PsiElement method = resolveResult.getElement();
        if (method instanceof PsiMethod && ((PsiMethod) method).isConstructor()) {
          final PsiParameter[] constructorParameters =
              ((PsiMethod) method).getParameterList().getParameters();
          if (constructorParameters.length > argIndex) {
            final PsiType toCastTo =
                resolveResult
                    .getSubstitutor()
                    .substitute(constructorParameters[argIndex].getType());
            result.add(SubtypeConstraint.create(toCastTo));
          }
        }
      }
    }
  }
  return result;
}

public int stateForName(String name, StateKind kind) {
  if (name.isEmpty()) {
    return DO_NOT_SAMPLE;
  }

  String counterName = prefix + name + "-msecs";
  synchronized (this) {
    Integer state = statesByName.get(counterName);
    if (state == null) {
      Counter<Long> counter =
          counterSetMutator.addCounter(Counter.longs(counterName, Counter.AggregationKind.SUM));
      state = countersByState.size();
      statesByName.put(name, state);
      countersByState.add(counter);
      kindsByState.put(state, kind);
    }
    StateKind originalKind = kindsByState.get(state);
    if (originalKind != kind) {
      throw new IllegalArgumentException(
          "for state named "
              + name
              + ", requested kind "
              + kind
              + " different from the original kind "
              + originalKind);
    }
    return state;
  }
}

