public void visit(final ParserRuleContext ancestorNode, final SQLStatement statement) {
  AlterTableStatement alterStatement = (AlterTableStatement) statement;

  ParserRuleContext modifyColumnCtx =
      TreeUtils.getFirstChildByRuleName(ancestorNode, "renameColumn");
  if (null == modifyColumnCtx) {
    return;
  }

  List<ParserRuleContext> columnNodes =
      TreeUtils.getAllDescendantByRuleName(modifyColumnCtx, "columnName");
  if (null == columnNodes || columnNodes.size() != 2) {
    return;
  }

  String oldName = columnNodes.get(0).getText();
  String newName = columnNodes.get(1).getText();
  ColumnDefinition oldDefinition = alterStatement.getUpdateColumns().remove(oldName);
  if (null != oldDefinition) {
    oldDefinition.setName(newName);
  } else {
    oldDefinition = new ColumnDefinition(newName, null, null, false);
  }

  alterStatement.getUpdateColumns().put(oldName, oldDefinition);
}

public static String getGoExecutablePath(@Nullable String sdkHomePath) {
  if (sdkHomePath != null) {
    if (isAppEngineSdkPath(sdkHomePath)) {
      String goExecutablePath = PathUtil.toSystemIndependentName(sdkHomePath);
      goExecutablePath =
          StringUtil.trimEnd(goExecutablePath, GoConstants.APP_ENGINE_GO_ROOT_DIRECTORY_PATH);

      boolean gcloudInstallation =
          sdkHomePath.endsWith(GoConstants.GCLOUD_APP_ENGINE_DIRECTORY_PATH);
      if (gcloudInstallation) {
        goExecutablePath =
            FileUtil.join(
                StringUtil.trimEnd(
