private static boolean shouldBeGenerated(PsiMethod method) {
  for (PsiMethod psiMethod : method.findSuperMethods()) {
    if (!psiMethod.hasModifierProperty(PsiModifier.ABSTRACT)) {
      final PsiType type = method.getReturnType();
      final PsiType superType = psiMethod.getReturnType();
      if (type != null && superType != null && !superType.isAssignableFrom(type)) {
        return false;
      }
    }
  }
  return true;
}

public PsiElement simplify(PsiMethodCallExpression call) {
  PsiExpression value = myValuePointer.getElement();
  if (value == null) return null;
  PsiMethodCallExpression qualifierCall = getQualifierMethodCall(call);
  if (qualifierCall == null) return null;
  PsiExpressionList qualifierArgs = qualifierCall.getArgumentList();
  CommentTracker ct = new CommentTracker();
  PsiReferenceParameterList typeParameters =
      qualifierCall.getMethodExpression().getParameterList();
  String typeParametersText = typeParameters == null ? "" : ct.text(typeParameters);
  PsiElement result =
      ct.replaceAndRestoreComments(
          call,
          CommonClassNames.JAVA_UTIL_ARRAYS
              + "."
              + typeParametersText
              + "asList"
              + ct.text(qualifierArgs)
              + ".contains("
              + ct.text(value)
              + ")");
  return JavaCodeStyleManager.getInstance(call.getProject()).shortenClassReferences(result);
}

protected static void linkifyText(final String text, final StringBuffer outputBuffer) {
  String prepared = text.replaceAll(Regex.BITCOIN_URI_PATTERN, "<a href=\"$0\">$0</a>");

  Matcher m = Regex.WEB_URL_PATTERN.matcher(prepared);
  while (m.find()) {
    int start = m.start();
    if (start == 0 || (start != 0 && prepared.charAt(start - 1) != '@')) {
      if (m.group().indexOf(':')
          > 0) { // With no URI-schema we may get "http:/" links with the second / missing
        m.appendReplacement(outputBuffer, "<a href=\"$0\">$0</a>");
      } else {
        m.appendReplacement(outputBuffer, "<a href=\"http://$0\">$0</a>");
      }
    } else {
      m.appendReplacement(outputBuffer, "$0");
    }
  }

  m.appendTail(outputBuffer);
}

public static void freeAll(Array objects) {
  if (objects == null) throw new IllegalArgumentException("objects cannot be null.");
  for (int i = 0, n = objects.size; i < n; i++) {
    Object object = objects.get(i);
    if (object == null) continue;
    ReflectionPool pool = typePools.get(object.getClass());
    if (pool == null) return; // Ignore freeing an object that was never retained.
    pool.free(object);
  }
}

