private void computePostdominators() {
  outer:
  for (Block block : postOrder()) {
    if (block.isLoopEnd()) {
      // We do not want the loop header registered as the postdominator of the loop end.
      continue;
    }
    if (block.getSuccessorCount() == 0) {
      // No successors => no postdominator.
      continue;
    }
    Block firstSucc = block.getSuccessors().get(0);
    if (block.getSuccessorCount() == 1) {
      block.postdominator = block;
      continue;
    }
    Block postdominator = firstSucc;
    for (Block sux : block.getSuccessors()) {
      postdominator = commonPostdominator(postdominator, sux);
      if (postdominator == null) {
        // There is a dead end => no postdominator available.
        continue outer;
      }
    }
    assert !block.getSuccessors().contains(postdominator)
        : "Block " + block + " has a wrong post dominator: " + postdominator;
    block.postdominator = postdominator;
  }
}

public void centerOnGraph() {
  float graphWidth = Math.abs(limits.getMaxXoctree() - limits.getMinXoctree());
  float graphHeight = Math.abs(limits.getMaxYoctree() - limits.getMinYoctree());

  float currentDistanceGraphRatioX =
      Math.abs(graphDrawable.viewport.get(2) / (float) graphDrawable.getDraggingMarkerX())
          / graphDrawable.cameraLocation[2];
  float currentDistanceGraphRatioY =
      Math.abs(graphDrawable.viewport.get(3) / (float) graphDrawable.getDraggingMarkerY())
          / graphDrawable.cameraLocation[2];
  float newCameraLocationX = graphWidth / currentDistanceGraphRatioX;
  float newCameraLocationY = graphHeight / currentDistanceGraphRatioY;
  float newCameraLocation = Math.max(newCameraLocationX, newCameraLocationY);

  graphDrawable.cameraLocation[0] = limits.getMinXoctree() + graphWidth / 2;
  graphDrawable.cameraLocation[1] = limits.getMinYoctree() + graphHeight / 2;
  graphDrawable.cameraLocation[2] = newCameraLocation;

  graphDrawable.cameraTarget[0] = graphDrawable.cameraLocation[0];
  graphDrawable.cameraTarget[1] = graphDrawable.cameraLocation[1];
  graphDrawable.cameraTarget[2] = 0;

  // Refresh
  engine.getScheduler().requireUpdateVisible();
}

private boolean getStringToReplace(
    int textOffset,
    int textEndOffset,
    Document document,
    FindModel findModel,
    Ref<String> stringToReplace)
    throws FindManager.MalformedReplacementStringException {
  if (textOffset < 0 || textOffset >= document.getTextLength()) {
    return false;
  }
  if (textEndOffset < 0 || textOffset > document.getTextLength()) {
    return false;
  }
  FindManager findManager = FindManager.getInstance(myProject);
  final CharSequence foundString =
      document.getCharsSequence().subSequence(textOffset, textEndOffset);
  PsiFile file = PsiDocumentManager.getInstance(myProject).getPsiFile(document);
  FindResult findResult =
      findManager.findString(
          document.getCharsSequence(),
          textOffset,
          findModel,
          file != null ? file.getVirtualFile() : null);
  if (!findResult.isStringFound()
      ||
      // find result should be in needed range
      !(findResult.getStartOffset() >= textOffset
          && findResult.getEndOffset() <= textEndOffset)) {
    return false;
  }

  stringToReplace.set(
      FindManager.getInstance(myProject)
          .getStringToReplace(foundString.toString(), findModel, textOffset, document.getText()));

  return true;
}

