public static void const2stack(
    CompilationResultBuilder crb,
    SPARCMacroAssembler masm,
    Value result,
    Register constantTableBase,
    Value input,
    SPARCDelayedControlTransfer delaySlotLir,
    JavaConstant constant) {
  if (constant.isDefaultForKind() || constant.isNull()) {
    SPARCAddress resultAddress = (SPARCAddress) crb.asAddress(result);
    emitStore(
        g0.asValue(LIRKind.combine(input)),
        resultAddress,
        result.getPlatformKind(),
        delaySlotLir,
        null,
        crb,
        masm);
  } else {
    try (ScratchRegister sc = masm.getScratchRegister()) {
      Value scratchRegisterValue = sc.getRegister().asValue(LIRKind.combine(constant));
      const2reg(
          crb,
          masm,
          scratchRegisterValue,
          constantTableBase,
          constant,
          SPARCDelayedControlTransfer.DUMMY);
      SPARCAddress resultAddress = (SPARCAddress) crb.asAddress(result);
      emitStore(
          scratchRegisterValue,
          resultAddress,
          result.getPlatformKind(),
          delaySlotLir,
          null,
          crb,
          masm);
    }
  }
}

private Vector3 calculateDirection(float angle) {
  Matrix4 transform = new Matrix4();
  Vector3 dir = new Vector3(-1, 0, 1).nor();
  float rotAngle = (float) Math.toDegrees(Math.asin(Math.tan(Math.toRadians(angle))));
  transform.setToRotation(new Vector3(1, 0, 1).nor(), rotAngle);
  dir.mul(transform).nor();
  return dir;
}

private boolean isSunUpAllDay(Calendar calendar, double latitude, double longitude) {
  Calendar cal = DateTimeUtils.truncateToMidnight(calendar);
  Sun sun = new Sun();
  for (int minutes = 0; minutes <= MINUTES_PER_DAY; minutes += CURVE_TIME_INTERVAL) {
    setSunPosition(cal, latitude, longitude, sun);
    if (sun.getPosition().getElevation() < SUN_ANGLE) {
      return false;
    }
    cal.add(Calendar.MINUTE, CURVE_TIME_INTERVAL);
  }
  return true;
}

private static void writeResourceBundle(PropertiesFile file, OutputStream out)
    throws IOException {
  if (file == null) {
    out.write(Amf3Types.NULL);
    return;
  }

  final AmfOutputStream amfOut = new AmfOutputStream(new ByteArrayOutputStreamEx(4 * 1024));
  // todo Embed, ClassReference, but idea doesn't support it too
  final List<IProperty> properties = file.getProperties();
  amfOut.write(Amf3Types.DICTIONARY);
  amfOut.writeUInt29((properties.size() << 1) | 1);
  amfOut.write(0);
  for (IProperty property : properties) {
    amfOut.write(property.getUnescapedKey());
    amfOut.write(property.getUnescapedValue());
  }

  amfOut.getByteArrayOut().writeTo(out);
}

