public final View getView(int position, View convertView, ViewGroup parent) {
  ContextualUndoView contextualUndoView = (ContextualUndoView) convertView;
  if (contextualUndoView == null) {
    contextualUndoView =
        new ContextualUndoView(parent.getContext(), mUndoLayoutId, mCountDownTextViewResId);
    contextualUndoView
        .findViewById(mUndoActionId)
        .setOnClickListener(new UndoListener(contextualUndoView));
  }

  View contentView = super.getView(position, contextualUndoView.getContentView(), parent);
  contextualUndoView.updateContentView(contentView);

  long itemId = getItemId(position);

  if (itemId == mCurrentRemovedId) {
    contextualUndoView.displayUndo();
    mCurrentRemovedView = contextualUndoView;
    long millisLeft = mAutoDeleteDelayMillis - (System.currentTimeMillis() - mDismissStartMillis);
    if (mCountDownFormatter != null) {
      mCurrentRemovedView.updateCountDownTimer(
          mCountDownFormatter.getCountDownString(millisLeft));
    }
  } else {
    contextualUndoView.displayContentView();
  }

  contextualUndoView.setItemId(itemId);
  return contextualUndoView;
}

private List<String> giveFieldBinding(FieldConstraint f) {
  List<String> result = new ArrayList<String>();
  if (f instanceof SingleFieldConstraint) {
    SingleFieldConstraint s = (SingleFieldConstraint) f;
    if (s.isBound() == true) {
      result.add(s.fieldBinding);
    }
  }
  if (f instanceof CompositeFieldConstraint) {
    CompositeFieldConstraint s = (CompositeFieldConstraint) f;
    for (FieldConstraint ss : s.constraints) {
      List<String> t = giveFieldBinding(s);
      result.addAll(t);
    }
  }
  return result;
}

protected boolean matchRequest(Request request) {

  // Has a session been created?
  Session session = request.getSessionInternal(false);
  if (session == null) return (false);

  // Is there a saved request?
  SavedRequest sreq = (SavedRequest) session.getNote(Constants.FORM_REQUEST_NOTE);
  if (sreq == null) return (false);

  // Is there a saved principal?
  if (session.getNote(Constants.FORM_PRINCIPAL_NOTE) == null) return (false);

  // Does the request URI match?
  String requestURI = request.getRequestURI();
  if (requestURI == null) return (false);
  return (requestURI.equals(request.getRequestURI()));
}

