}

public <K extends ActionResponse> K execute(ActionRequestBuilder request) {
  StopWatch fullProfile = profiling.start("search", Profiling.Level.FULL);
  K response = null;
  try {

    response = (K) request.get();

    if (profiling.isProfilingEnabled(Profiling.Level.BASIC)) {
      if (ToXContent.class.isAssignableFrom(request.getClass())) {
        XContentBuilder debugResponse = XContentFactory.jsonBuilder();
        debugResponse.startObject();
        ((ToXContent) request).toXContent(debugResponse, ToXContent.EMPTY_PARAMS);
        debugResponse.endObject();
        fullProfile.stop("ES Request: %s", debugResponse.string());
      } else {
        fullProfile.stop("ES Request: %s", request.toString().replaceAll("\n", ""));
      }
    }
    return response;
  } catch (Exception e) {
    LOGGER.error("could not execute request: " + request, e);
    throw new IllegalStateException("ES error: ", e);
  }
}

private KadaneResult kadane(int arr[]) {
  int max = 0;
  int maxStart = -1;
  int maxEnd = -1;
  int currentStart = 0;
  int maxSoFar = 0;
  for (int i = 0; i < arr.length; i++) {
    maxSoFar += arr[i];
    if (maxSoFar < 0) {
      maxSoFar = 0;
      currentStart = i + 1;
    }
    if (max < maxSoFar) {
      maxStart = currentStart;
      maxEnd = i;
      max = maxSoFar;
    }
  }
  return new KadaneResult(max, maxStart, maxEnd);
}

public void addAllUpdates(RightTupleSets tupleSets) {
  RightTupleSetsImpl tupleSetsImpl = (RightTupleSetsImpl) tupleSets;
  if (updateFirst == null) {
    updateFirst = tupleSetsImpl.getUpdateFirst();
    updateSize = tupleSetsImpl.updateSize;
  } else {
    RightTuple current = updateFirst;
    RightTuple last = null;
    while (current != null) {
      last = current;
      current = current.getStagedNext();
    }
    RightTuple rightTuple = tupleSetsImpl.getUpdateFirst();
    last.setStagedNext(rightTuple);
    rightTuple.setStagePrevious(rightTuple);
    updateSize = updateSize + tupleSetsImpl.updateSize();
  }
}

public static Block createLongDictionaryBlock(int start, int length) {
  int dictionarySize = length / 5;
  BlockBuilder builder = BIGINT.createBlockBuilder(new BlockBuilderStatus(), dictionarySize);
  for (int i = start; i < dictionarySize; i++) {
    BIGINT.writeLong(builder, i);
  }
  int[] ids = new int[length];
  for (int i = 0; i < length; i++) {
    ids[i] = i % dictionarySize;
  }
  return new DictionaryBlock(length, builder.build(), wrappedIntArray(ids));
}

