public void doWithLibraries(LibraryCallback callback) throws IOException {

  FileCollection compile = this.project.getConfigurations().getByName("compile");

  FileCollection runtime = this.project.getConfigurations().getByName("runtime");
  runtime = runtime.minus(compile);

  FileCollection provided =
      this.project.getConfigurations().findByName(this.providedConfigurationName);
  if (provided != null) {
    compile = compile.minus(provided);
    runtime = compile.minus(provided);
  }

  libraries(LibraryScope.COMPILE, compile, callback);
  libraries(LibraryScope.RUNTIME, runtime, callback);
  libraries(LibraryScope.PROVIDED, provided, callback);
}

protected void filterResults(List<AuxiliaryResolveInfo> results) {
  // only do work if ordering is enabled [most of the time it won't be]
  if (mOrderResult.size() == 0) {
    return;
  }
  int resultSize = results.size();
  for (int i = 0; i < resultSize; i++) {
    final InstantAppResolveInfo info = results.get(i).resolveInfo;
    final String packageName = info.getPackageName();
    final Pair<Integer, InstantAppResolveInfo> savedInfo = mOrderResult.get(packageName);
    if (savedInfo == null) {
      // package doesn't having ordering
      continue;
    }
    if (savedInfo.second == info) {
      // circled back to the highest ordered item; remove from order list
      mOrderResult.remove(packageName);
      if (mOrderResult.size() == 0) {
        // no more ordered items
        break;
      }
      continue;
    }
    // item has a worse order, remove it from the result list
    results.remove(i);
    resultSize--;
    i--;
  }
}

