public static int indexOf(String source, int ch, int origFromIndex) {
  int fromIndex = origFromIndex;
  final int sourceCount = source.length();
  if (fromIndex >= sourceCount) {
    // Note: fromIndex might be near -1>>>1.
    return -1;
  }
  if (fromIndex < 0) {
    fromIndex = 0;
  }

  if (ch < Character.MIN_SUPPLEMENTARY_CODE_POINT) {
    char[] sourceArray = StringSubstitutions.getValue(source);

    Pointer sourcePointer =
        Word.objectToTrackedPointer(sourceArray)
            .add(charArrayBaseOffset(INJECTED))
            .add(fromIndex * charArrayIndexScale(INJECTED));
    int result =
        AMD64ArrayIndexOfNode.optimizedArrayIndexOf(
            sourcePointer, sourceCount - fromIndex, (char) ch, JavaKind.Char);
    if (result != -1) {
      return result + fromIndex;
    }
    return result;
  } else {
    return indexOf(source, ch, fromIndex);
  }
}

protected static int calculateShift(int minimumValue, int maximumValue) {
  int shift = 0;
  int value = 1;
  while (value < minimumValue && value < minimumValue) {
    value <<= 1;
    shift++;
  }
  return shift;
}

public static Throwable unwrapCause(Throwable t) {
  int counter = 0;
  Throwable result = t;
  while (result instanceof ElasticSearchWrapperException) {
    if (t.getCause() == null) {
      return result;
    }
    if (t.getCause() == t) {
      return result;
    }
    if (counter++ > 10) {
      // dear god, if we got more than 10 levels down, WTF? just bail
      logger.warn("Exception cause unwrapping ran for 10 levels...", t);
      return result;
    }
    result = t.getCause();
  }
  return result;
}

