public void loadPlugin(File file) throws Exception {
  Preconditions.checkNotNull(file, "file");
  Preconditions.checkArgument(file.isFile(), "Must load from file");

  try (JarFile jar = new JarFile(file)) {
    JarEntry pdf = jar.getJarEntry("plugin.yml");
    Preconditions.checkNotNull(pdf, "Plugin must have a plugin.yml");

    try (InputStream in = jar.getInputStream(pdf)) {
      PluginDescription desc = yaml.loadAs(in, PluginDescription.class);
      URLClassLoader loader = new URLClassLoader(new URL[] {file.toURI().toURL()});
      Class<?> main = loader.loadClass(desc.getMain());
      Plugin plugin = (Plugin) main.getDeclaredConstructor().newInstance();

      plugin.init(desc);
      plugins.put(desc.getName(), plugin);
      plugin.onLoad();
      ProxyServer.getInstance()
          .getLogger()
          .log(
              Level.INFO,
              "Loaded plugin {0} version {1} by {2}",
              new Object[] {desc.getName(), desc.getVersion(), desc.getAuthor()});
    }
  }
}

private List<Transaction> loadErrorTransactions() {

  TransactionRepository transactionRepository =
      transactionConfigurator.getTransactionRepository();

  long currentTimeInMillis = Calendar.getInstance().getTimeInMillis();

  List<Transaction> transactions =
      transactionRepository.findAllUnmodifiedSince(
          new Date(
              currentTimeInMillis
                  - transactionConfigurator.getRecoverConfig().getRecoverDuration() * 1000));

  List<Transaction> recoverTransactions = new ArrayList<Transaction>();

  for (Transaction transaction : transactions) {

    int result = transactionRepository.update(transaction);

    if (result > 0) {
      recoverTransactions.add(transaction);
    }
  }

  return recoverTransactions;
}

public boolean addGeometry(Geometry geometry) {
  Texture diffuse = getMaterialTexture(geometry, "DiffuseMap");
  Texture normal = getMaterialTexture(geometry, "NormalMap");
  Texture specular = getMaterialTexture(geometry, "SpecularMap");
  if (diffuse == null) {
    diffuse = getMaterialTexture(geometry, "ColorMap");
  }
  if (diffuse != null && diffuse.getKey() != null) {
    String keyName = diffuse.getKey().toString();
    if (!addTexture(diffuse, "DiffuseMap")) {
      return false;
    } else {
      if (normal != null && normal.getKey() != null) {
        addTexture(normal, "NormalMap", keyName);
      }
      if (specular != null && specular.getKey() != null) {
        addTexture(specular, "SpecularMap", keyName);
      }
    }
    return true;
  }
  return true;
}

public static Throwable unwrapThrowable(Throwable wrapped) {
  Throwable unwrapped = wrapped;
  while (true) {
    if (unwrapped instanceof InvocationTargetException) {
      unwrapped = ((InvocationTargetException) wrapped).getTargetException();
    } else if (unwrapped instanceof UndeclaredThrowableException) {
      unwrapped = ((UndeclaredThrowableException) wrapped).getUndeclaredThrowable();
    } else {
      return unwrapped;
    }
  }
}

protected double getDropoutFromConfig(Map<String, Object> layerConfig)
    throws InvalidKerasConfigurationException {
  Map<String, Object> innerConfig = getInnerLayerConfigFromConfig(layerConfig);
  /* NOTE: Keras "dropout" parameter determines dropout probability,
   * while DL4J "dropout" parameter determines retention probability.
   */
  double dropout = 1.0;
  if (innerConfig.containsKey(LAYER_FIELD_DROPOUT)) {
    /* For most feedforward layers. */
    dropout = 1.0 - (double) innerConfig.get(LAYER_FIELD_DROPOUT);
  } else if (layerConfig.containsKey(LAYER_FIELD_DROPOUT_W)) {
    /* For LSTMs. */
    dropout = 1.0 - (double) layerConfig.get(LAYER_FIELD_DROPOUT_W);
  }
  return dropout;
}

