protected String retrieveFinalFormKey(
    String formKey,
    FlowElement flowElement,
    Map<Long, Form> formIdMap,
    AbstractModel model,
    Long appDefinitionId,
    Long appDeploymentId,
    User user) {

  String finalFormKey = null;
  List<ExtensionElement> formIdExtensions =
      flowElement.getExtensionElements().get("form-reference-id");
  List<ExtensionElement> formNameExtensions =
      flowElement.getExtensionElements().get("form-reference-name");
  if (CollectionUtils.isNotEmpty(formIdExtensions)
      && CollectionUtils.isNotEmpty(formNameExtensions)) {
    Long formId = Long.valueOf(formIdExtensions.get(0).getElementText());
    finalFormKey =
        getFormKeyWithFormId(formId, formIdMap, model, appDefinitionId, appDeploymentId, user);

  } else if (StringUtils.isNotEmpty(formKey) && formKey.startsWith("FORM_REFERENCE")) {
    String formIdValue = formKey.replace("FORM_REFERENCE", "");
    if (NumberUtils.isNumber(formIdValue)) {
      Long formId = Long.valueOf(formIdValue);
      finalFormKey =
          getFormKeyWithFormId(formId, formIdMap, model, appDefinitionId, appDeploymentId, user);
    }
  }

  if (StringUtils.isEmpty(formKey)) {
    finalFormKey = formKey;
  }

  return finalFormKey;
}

