public URL getResource(String name) {
  if (_notFound.contains(name)) {
    if (LOG.isDebugEnabled()) LOG.debug("Not found cache hit resource {}", name);
    return null;
  }

  URL url = _cache.get(name);

  if (url == null) {
    // Not found in cache, try parent
    url = super.getResource(name);

    if (url == null) {
      // Still not found, cache the not-found result
      if (LOG.isDebugEnabled()) LOG.debug("Caching not found resource {}", name);
      _notFound.add(name);
    } else {
      // Cache the new result
      _cache.putIfAbsent(name, url);
    }
  }

  return url;
}

