public boolean removeListener(final String eventId, final IDownloadListener listener) {
  if (FileDownloadLog.NEED_LOG) {
    FileDownloadLog.v(this, "removeListener %s", eventId);
  }

  LinkedList<IDownloadListener> container = listenersMap.get(eventId);
  if (container == null) {
    synchronized (eventId.intern()) {
      container = listenersMap.get(eventId);
    }
  }

  if (container == null || listener == null) {
    return false;
  }

  synchronized (eventId.intern()) {
    boolean succeed = container.remove(listener);
    if (container.size() <= 0) {
      listenersMap.remove(eventId);
    }
    return succeed;
  }
}

public InstallState getInstallState(Version system) {
  if (StringUtils.isEmpty(installedRelease)) {
    return InstallState.NOT_INSTALLED;
  }
  Version ir = Version.createVersion(installedRelease);
  Version cr = Version.ZERO;
  PluginRelease curr = getCurrentRelease(system);
  if (curr != null) {
    cr = Version.createVersion(curr.version);
  }
  switch (ir.compareTo(cr)) {
    case -1:
      return InstallState.UNKNOWN;
    case 1:
      return InstallState.UPDATE_AVAILABLE;
    default:
      return InstallState.INSTALLED;
  }
}

public Key importKey(@NonNull String alias, byte[] keyBytes)
    throws InternalRecoveryServiceException, LockScreenRequiredException {
  try {
    String grantAlias = mBinder.importKey(alias, keyBytes);
    if (grantAlias == null) {
      throw new InternalRecoveryServiceException("Null grant alias");
    }
    return getKeyFromGrant(grantAlias);
  } catch (RemoteException e) {
    throw e.rethrowFromSystemServer();
  } catch (UnrecoverableKeyException e) {
    throw new InternalRecoveryServiceException("Failed to get key from keystore", e);
  } catch (ServiceSpecificException e) {
    if (e.errorCode == ERROR_INSECURE_USER) {
      throw new LockScreenRequiredException(e.getMessage());
    }
    throw wrapUnexpectedServiceSpecificException(e);
  }
}

