private int recursiveCollapse(@IntRange(from = 0) int position) {
  T item = getItem(position);
  if (!isExpandable(item)) {
    return 0;
  }
  IExpandable expandable = (IExpandable) item;
  int subItemCount = 0;
  if (expandable.isExpanded()) {
    List<T> subItems = expandable.getSubItems();
    for (int i = subItems.size() - 1; i >= 0; i--) {
      T subItem = subItems.get(i);
      int pos = getItemPosition(subItem);
      if (pos < 0) {
        continue;
      }
      if (subItem instanceof IExpandable) {
        subItemCount += recursiveCollapse(pos);
      }
      mData.remove(subItem);
      subItemCount++;
    }
  }
  return subItemCount;
}

public static void unboxInlineClass(
    @NotNull Type type,
    @NotNull KotlinType targetInlineClassType,
    @NotNull InstructionAdapter v) {
  Type owner = KotlinTypeMapper.mapInlineClassTypeAsDeclaration(targetInlineClassType);

  coerce(type, owner, v);

  Type resultType = KotlinTypeMapper.mapUnderlyingTypeOfInlineClassType(targetInlineClassType);

  if (TypeUtils.isNullableType(targetInlineClassType) && !isPrimitive(type)) {
    boxOrUnboxWithNullCheck(v, vv -> invokeUnboxMethod(vv, owner, resultType));
  } else {
    invokeUnboxMethod(v, owner, resultType);
  }
}

private static Set<WorkerSlot> badSlots(
    Map<WorkerSlot, List<ExecutorDetails>> existingSlots, int numExecutors, int numWorkers) {
  if (numWorkers != 0) {
    Map<Integer, Integer> distribution = Utils.integerDivided(numExecutors, numWorkers);
    Set<WorkerSlot> slots = new HashSet<WorkerSlot>();

    for (Entry<WorkerSlot, List<ExecutorDetails>> entry : existingSlots.entrySet()) {
      Integer executorCount = entry.getValue().size();
      Integer workerCount = distribution.get(executorCount);
      if (workerCount != null && workerCount > 0) {
        slots.add(entry.getKey());
        workerCount--;
        distribution.put(executorCount, workerCount);
      }
    }

    for (WorkerSlot slot : slots) {
      existingSlots.remove(slot);
    }

    return existingSlots.keySet();
  }

  return null;
}

