public void testClear() {
  BooleanSubscription s1 = new BooleanSubscription();
  BooleanSubscription s2 = new BooleanSubscription();

  CompositeSubscription s = new CompositeSubscription();
  s.add(s1);
  s.add(s2);

  assertFalse(s1.isUnsubscribed());
  assertFalse(s2.isUnsubscribed());

  s.clear();

  assertTrue(s1.isUnsubscribed());
  assertTrue(s2.isUnsubscribed());
  assertFalse(s.isUnsubscribed());

  BooleanSubscription s3 = new BooleanSubscription();

  s.add(s3);
  s.unsubscribe();

  assertTrue(s3.isUnsubscribed());
  assertTrue(s.isUnsubscribed());
}

public AsyncSupport resolve(
    boolean useNativeIfPossible, boolean defaultToBlocking, boolean useServlet30Async) {
  AsyncSupport cs = null;

  // Validate the value for old Servlet Container.
  useServlet30Async = testClassExists(SERVLET_30);

  if (!defaultToBlocking) {
    List<Class<? extends AsyncSupport>> l =
        detectWebSocketPresent(useNativeIfPossible, useServlet30Async);

    if (!l.isEmpty()) {
      cs = resolveWebSocket(l);
    }
  }

  if (cs == null) {
    AsyncSupport nativeSupport = resolveNativeCometSupport(detectContainersPresent());
    return nativeSupport == null ? defaultCometSupport(defaultToBlocking) : nativeSupport;
  } else {
    return cs;
  }
}

private void cleanDirectory(final VirtualFile dir) {
  new WriteCommandAction(myProject) {
    protected void run(Result result) throws Throwable {
      deleteChildrenRecursively(dir);
    }

    private void deleteChildrenRecursively(final VirtualFile dir) throws IOException {
      for (final VirtualFile child : dir.getChildren()) {
        if (child.isDirectory()) {
          deleteChildrenRecursively(child);
        }
        TranslatingCompilerFilesMonitor.removeSourceInfo(child);
        child.delete(this);
      }
    }
  }.execute();
}

