Map<VirtualFile, Object[]> getDirectInheritors(
    @NotNull LightRef searchElement,
    @NotNull GlobalSearchScope searchScope,
    @NotNull GlobalSearchScope dirtyScope,
    @NotNull FileType fileType,
    @NotNull CompilerHierarchySearchType searchType)
    throws StorageException {
  GlobalSearchScope effectiveSearchScope =
      GlobalSearchScope.notScope(dirtyScope).intersectWith(searchScope);
  LanguageLightRefAdapter adapter = CompilerReferenceServiceImpl.findAdapterForFileType(fileType);
  LOG.assertTrue(adapter != null, "adapter is null for file type: " + fileType);
  Class<? extends LightRef> requiredLightRefClass = searchType.getRequiredClass(adapter);

  Map<VirtualFile, Object[]> candidatesPerFile = new HashMap<>();
  myIndex
      .get(CompilerIndices.BACK_HIERARCHY)
      .getData(searchElement)
      .forEach(
          (fileId, defs) -> {
            final List<LightRef> requiredCandidates =
                defs.stream().filter(requiredLightRefClass::isInstance).collect(toList());
            if (requiredCandidates.isEmpty()) return true;
            final VirtualFile file = findFile(fileId);
            if (file != null && effectiveSearchScope.contains(file)) {
              candidatesPerFile.put(
                  file, searchType.convertToIds(requiredCandidates, myIndex.getByteSeqEum()));
            }
            return true;
          });
  return candidatesPerFile.isEmpty() ? Collections.emptyMap() : candidatesPerFile;
}

public static net.osmand.Location convertLocation(Location l, OsmandApplication app) {
  net.osmand.Location r = new net.osmand.Location(l.getProvider());
  r.setLatitude(l.getLatitude());
  r.setLongitude(l.getLongitude());
  r.setTime(l.getTime());
  if (l.hasAccuracy()) {
    r.setAccuracy(l.getAccuracy());
  }
  if (l.hasSpeed()) {
    r.setSpeed(l.getSpeed());
  }
  if (l.hasAltitude()) {
    r.setAltitude(l.getAltitude());
  }
  if (l.hasBearing()) {
    r.setBearing(l.getBearing());
  }
  if (l.hasAltitude() && app != null) {
    double alt = l.getAltitude();
    final GeoidAltitudeCorrection geo = app.getResourceManager().getGeoidAltitudeCorrection();
    if (geo != null) {
      alt -= geo.getGeoidHeight(l.getLatitude(), l.getLongitude());
      r.setAltitude(alt);
    }
  }
  return r;
}

protected <T> void writeBody(
    T o,
    Message outMessage,
    Class<?> cls,
    Type type,
    Annotation[] anns,
    MultivaluedMap<String, Object> headers,
    OutputStream os) {

  if (o == null) {
    return;
  }
  @SuppressWarnings("unchecked")
  Class<T> theClass = (Class<T>) cls;

  MediaType contentType = MediaType.valueOf(headers.getFirst("Content-Type").toString());

  MessageBodyWriter<T> mbw =
      ProviderFactory.getInstance(outMessage)
          .createMessageBodyWriter(theClass, type, anns, contentType, outMessage);
  if (mbw != null) {
    try {
      mbw.writeTo(o, cls, type, anns, contentType, headers, os);
      if (os != null) {
        os.flush();
      }
    } catch (Exception ex) {
      reportMessageHandlerProblem("MSG_WRITER_PROBLEM", cls, contentType, ex, null);
    }
  } else {
    reportMessageHandlerProblem("NO_MSG_WRITER", cls, contentType, null, null);
  }
}

public Map<Long, List<Layout>> getLayoutChildLayouts(
    LayoutSet layoutSet, List<Layout> parentLayouts) {

  List<Layout> childLayouts =
      _getChildLayouts(layoutSet, ListUtil.toLongArray(parentLayouts, Layout::getLayoutId));

  Map<Long, List<Layout>> layoutChildLayouts = new HashMap<>();

  for (Layout childLayout : childLayouts) {
    List<Layout> layoutChildLayoutsList =
        layoutChildLayouts.computeIfAbsent(
            childLayout.getParentLayoutId(), (parentLayoutId) -> new ArrayList<>());

    layoutChildLayoutsList.add(childLayout);
  }

  for (List<Layout> layoutChildLayoutsList : layoutChildLayouts.values()) {

    layoutChildLayoutsList.sort(Comparator.comparing(Layout::getPriority));
  }

  return layoutChildLayouts;
}

protected Principal getPrincipal(String username) {

  User user = database.findUser(username);
  if (user == null) {
    return null;
  }

  List roles = new ArrayList();
  Iterator uroles = user.getRoles();
  while (uroles.hasNext()) {
    Role role = (Role) uroles.next();
    roles.add(role.getName());
  }
  Iterator groups = user.getGroups();
  while (groups.hasNext()) {
    Group group = (Group) groups.next();
    uroles = group.getRoles();
    while (uroles.hasNext()) {
      Role role = (Role) uroles.next();
      roles.add(role.getName());
    }
  }
  return new GenericPrincipal(this, username, user.getPassword(), roles, user);
}

public static View findParentViewHolderItemView(View v) {
  final ViewParent parent = v.getParent();
  if (parent instanceof RecyclerView) {
    // returns the passed instance if the parent is RecyclerView
    return v;
  } else if (parent instanceof View) {
    // check the parent view recursively
    return findParentViewHolderItemView((View) parent);
  } else {
    return null;
  }
}

public final String resolveRelativePathEntry(String path, String relativeTo) {
  if (relativeTo == null) {
    return AbsolutePathService.convertPath(path);
  } else {
    PathEntry pathEntry;
    synchronized (pathEntries) {
      pathEntry = pathEntries.get(relativeTo);
      if (pathEntry == null) {
        throw MESSAGES.pathEntryNotFound(path);
      }
      return RelativePathService.doResolve(pathEntry.resolvePath(), path);
    }
  }
}

