public ManagementStrategy create(CamelContext context, boolean disableJMX) {
  ManagementStrategy answer = null;

  if (disableJMX || Boolean.getBoolean(JmxSystemPropertyKeys.DISABLED)) {
    log.info("JMX is disabled.");
  } else {
    try {
      ManagedManagementStrategy jmx =
          new ManagedManagementStrategy(new DefaultManagementAgent(context));
      // must start it to ensure JMX works and can load needed Spring JARs
      ServiceHelper.startService(jmx);
      // prefer to have it at first strategy
      context.getLifecycleStrategies().add(0, new DefaultManagementLifecycleStrategy(context));
      log.info("JMX enabled.");
    } catch (Exception e) {
      answer = null;
      log.warn("Cannot create JMX lifecycle strategy. Will fallback and disable JMX.", e);
    }
  }

  if (answer == null) {
    answer = new DefaultManagementStrategy();
  }
  return answer;
}

public boolean satisfiedBy(PsiElement element) {
  final PsiElement parent = element.getParent();
  if (!(parent instanceof GrTypeDefinition)) return false;
  if (((GrTypeDefinition) parent).getNameIdentifierGroovy() != element) return false;

  final String name = ((GrTypeDefinition) element).getName();
  if (name == null || name.length() == 0) return false;
  final PsiFile file = element.getContainingFile();
  if (!(file instanceof GroovyFile)) return false;
  if (!file.isPhysical()) return false;
  if (name.equals(FileUtil.getNameWithoutExtension(file.getName()))) return false;
  if (mySearchForClassInMultiClassFile) {
    return ((GroovyFile) file).getClasses().length > 1;
  } else {
    return !((GroovyFile) file).isScript();
  }
}

Object invokeAndReturn(HttpServletRequest httpRequest, T controller) throws IOException {
  final String partParameter = HttpParameter.PART.getParameterFrom(httpRequest);
  final HttpPart httpPart = HttpPart.getByName(partParameter);
  final Method method = methodsByPart.get(httpPart);
  if (method == null) {
    throw new IllegalArgumentException("Unknown http part: " + partParameter);
  }
  try {
    // find parameters values
    final Object[] parameterValues = getParameterValues(httpRequest, method);
    // invoke the method (the "endpoint")
    return method.invoke(controller, parameterValues);
  } catch (final IllegalAccessException e) {
    throw new IllegalStateException(e);
  } catch (final InvocationTargetException e) {
    final Throwable targetException = e.getTargetException();
    if (targetException instanceof IOException) {
      throw (IOException) targetException;
    } else if (targetException instanceof RuntimeException) {
      throw (RuntimeException) targetException;
    } else if (targetException instanceof Error) {
      throw (Error) targetException;
    }
    throw new IOException(e);
  }
}

public double distance(double centroidLengthSquare, Vector centroid, Vector v) {
  if (centroid.size() != v.size()) {
    throw new CardinalityException();
  }

  double result = centroidLengthSquare;
  result += v.getDistanceSquared(centroid);
  return result;
}

  private ClassNode configureWildcardType(WildcardType wildcardType) {
    ClassNode base = ClassHelper.makeWithoutCaching("?");
    base.setRedirect(ClassHelper.OBJECT_TYPE);
    // TODO: more than one lower bound for wildcards?
    ClassNode[] lowers = configureTypes(wildcardType.getLowerBounds());
    ClassNode lower = null;
    // TODO: is it safe to remove this? What was the original intention?
    if (lower != null) lower = lowers[0];

    ClassNode[] upper = configureTypes(wildcardType.getUpperBounds());
    GenericsType t = new GenericsType(base, upper, lower);
