protected void processTypeSwitch(AbstractBeginNode beginNode, TypeSwitchNode typeSwitch) {
  ValueNode hub = typeSwitch.value();
  if (hub instanceof LoadHubNode) {
    LoadHubNode loadHub = (LoadHubNode) hub;
    ValueNode value = loadHub.getValue();
    if (maybeMultipleUsages(value)) {
      Stamp stamp = null;
      for (int i = 0; i < typeSwitch.keyCount(); i++) {
        if (typeSwitch.keySuccessor(i) == beginNode) {
          if (stamp == null) {
            stamp =
                StampFactory.objectNonNull(
                    TypeReference.createExactTrusted(typeSwitch.typeAt(i)));
          } else {
            stamp =
                stamp.meet(
                    StampFactory.objectNonNull(
                        TypeReference.createExactTrusted(typeSwitch.typeAt(i))));
          }
        }
      }
      if (stamp != null) {
        registerNewStamp(value, stamp, beginNode);
      }
    }
  }
}

public ScheduledEntry<K, V> cancelComparingTimeKey(K key) {
  Set<TimeKey> candidateKeys = new HashSet<TimeKey>();
  for (Object tkey : secondsOfKeys.keySet()) {
    TimeKey timeKey = (TimeKey) tkey;
    if (timeKey.getKey().equals(key)) {
      candidateKeys.add(timeKey);
    }
  }

  ScheduledEntry<K, V> result = null;
  for (TimeKey timeKey : candidateKeys) {
    final Integer second = secondsOfKeys.remove(timeKey);
    if (second != null) {
      final ConcurrentMap<Object, ScheduledEntry<K, V>> entries = scheduledEntries.get(second);
      if (entries != null) {
        result = entries.remove(timeKey);
      }
    }
  }

  return result;
}

